mapper.update:
update board set 
title = #{title}, content = #{content},
ageType = #{ageType}, date = sysdate(),
viewNo = #{viewNo}
where boardNo = #{boardNo} and id_index = #{id_index}

update board set
recommend =recommend +1
where boardNo = #{boardNo}

mapper.select:
select *,(select count(*) from comment where comment.boardNo =board.boardNo)as commentNum from board

	
		where ageType >= #{ageType}
	
	
		where ageType = #{ageType}

select count(*) from board

	
		where ageType >= #{ageType}
	
	
		where ageType = #{ageType}
	


	where title like concat("%", #{content} ,"%")


	where nick like concat("%", #{content} ,"%")
